styles:
.mk-checkbox-wrap {
  display: flex;
  flex-wrap: wrap;
  margin-right: 8px;
  margin-bottom: 8px;
}
.mk-checkbox-group-wrap {
  display: flex;
  flex-wrap: wrap;
}
ul, menu, dir {
  display: block;
  list-style-type: disc;
  -webkit-margin-before: 1em;
  -webkit-margin-after: 1em;
  -webkit-margin-start: 0px;
  -webkit-margin-end: 0px;
  -webkit-padding-start: 0px!important;
}
.checkbox-list { list-style-type: none; }
.horizontal-list > li,
.horizontal > li {
  display: inline-block;
  margin-right: 10px;
  zoom: 1;
}
.checkbox-name { white-space: nowrap; }
.badge {
  display: inline-block;
  min-width: 10px;
  padding: 3px 7px;
  font-size: 12px;
  font-weight: bold;
  line-height: 1;
  color: #fff;
  text-align: center;
  white-space: nowrap;
  vertical-align: baseline;
  background-color: #232890;
  border-radius: 10px;
}
